INSERT INTO `weibo` VALUES ('5166650921060873', 'PrXTu4rYR', 'https://m.weibo.cn/detail/5166650921060873', '7876646016', '未来科技', '#智能仿生鱼已投入长江保护#【武大造“智能仿生鱼”亮相，#牛津大学院士对智能仿生鱼爱不释手#】5月14日，教育部、中国联合国教科文组织全国委员会、湖北省人民政府湖北省武汉市共同举办的2025世界数字教育大会正式开幕，湖北多所高校的教育数字化成果展览在@武汉大学展出。这款由武汉大学研发的智能仿生鱼，运用柔性硅胶材质，结合仿生流体力学精心设计，能精准模拟12种淡水鱼类的灵动游姿。其头部配备了先进的避障传感器，让仿生鱼在复杂的水下环境中也能自由穿梭，巧妙避开障碍物。目前，这些智能仿生鱼已在长江中游建立起30个智能监测节点，以每秒1.2米的稳定游速，默默执行着生态监测任务。它们的摆尾动作与真鱼毫无二致，在不干扰鱼群正常生活的情况下，累计传回的生态数据已超50TB，目前已在长江保护中，用于生态数据采集。未来科技的微博视频', '智能仿生鱼已投入长江保护;牛津大学院士对智能仿生鱼爱不释手', '武汉大学', '', 'https://f.video.weibocdn.com/o0/XUlbUF0tlx08ogFnwNWw010412004lKl0E010.mp4?label=mp4_hd&template=540x960.24.0&ori=0&ps=1BThihd3VLAY5R&Expires=1747364835&ssig=LwVmoe0z0q&KID=unistore,video', '', '2025-05-15 00:00:00', '微博视频号', 0, 0, 0, '', 1);
